	$mfhclass->db->install_queries[] = "CREATE TABLE `{$mfhclass->input->post_vars['access_name']}_attachments` (
	  `attach_id` mediumint(8) unsigned NOT NULL auto_increment,
	  `post_msg_id` mediumint(8) unsigned NOT NULL default '0',
	  `topic_id` mediumint(8) unsigned NOT NULL default '0',
	  `in_message` tinyint(1) unsigned NOT NULL default '0',
	  `poster_id` mediumint(8) unsigned NOT NULL default '0',
	  `is_orphan` tinyint(1) unsigned NOT NULL default '1',
	  `physical_filename` varchar(255)  NOT NULL default '',
	  `real_filename` varchar(255)  NOT NULL default '',
	  `download_count` mediumint(8) unsigned NOT NULL default '0',
	  `attach_comment` text  NOT NULL,
	  `extension` varchar(100)  NOT NULL default '',
	  `mimetype` varchar(100)  NOT NULL default '',
	  `filesize` int(20) unsigned NOT NULL default '0',
	  `filetime` int(11) unsigned NOT NULL default '0',
	  `thumbnail` tinyint(1) unsigned NOT NULL default '0',
	  PRIMARY KEY  (`attach_id`),
	  KEY `filetime` (`filetime`),
	  KEY `post_msg_id` (`post_msg_id`),
	  KEY `topic_id` (`topic_id`),
	  KEY `poster_id` (`poster_id`),
	  KEY `is_orphan` (`is_orphan`)
	) ENGINE=MyISAM ;";

	$mfhclass->db->install_queries[] = "CREATE TABLE `{$mfhclass->input->post_vars['access_name']}_banlist` (
	  `ban_id` mediumint(8) unsigned NOT NULL auto_increment,
	  `ban_userid` mediumint(8) unsigned NOT NULL default '0',
	  `ban_ip` varchar(40)  NOT NULL default '',
	  `ban_email` varchar(100)  NOT NULL default '',
	  `ban_start` int(11) unsigned NOT NULL default '0',
	  `ban_end` int(11) unsigned NOT NULL default '0',
	  `ban_exclude` tinyint(1) unsigned NOT NULL default '0',
	  `ban_reason` varchar(255)  NOT NULL default '',
	  `ban_give_reason` varchar(255)  NOT NULL default '',
	  PRIMARY KEY  (`ban_id`),
	  KEY `ban_end` (`ban_end`),
	  KEY `ban_user` (`ban_userid`,`ban_exclude`),
	  KEY `ban_email` (`ban_email`,`ban_exclude`),
	  KEY `ban_ip` (`ban_ip`,`ban_exclude`)
	) ENGINE=MyISAM ;";

